export const generateFitnessPlan = (planData) => {
  const input = {
    model: "gpt-3.5-turbo",
    messages: [
      { role: "system", content: "You are a fitness plan generator." },
      {
        role: "user",
        content: `Generate a fitness plan for me. Name: ${
          planData.name
        }, Gender: ${
          planData.gender
        }, Days of the Week: ${planData.daysOfWeek.join(", ")}, Body Part: ${
          planData.bodyPart
        }, Time Range: ${planData.timeRange}, Fitness Level: ${
          planData.fitnessLevel
        }`,
      },
    ],
  };
  return fetch("/results.js", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer YOUR_API_KEY", // Replace with actual API key
    },
    body: JSON.stringify(input),
  })
    .then((response) => response.json())
    .then((data) => data.choices[0].message.content)
    .catch((error) => {
      console.error(error);
      throw error;
    });
};
