import "./App.css";
import Output from "./components/ResultsPage/Output";

import { BrowserRouter } from "react-router-dom";
import Router from './Router'


function App() {
  // use useEffect to apply AOS animation
  useEffect(() => {
    AOS.init({
      duration: 1000,
      offset: 100,
    });
    AOS.refresh();
  }, []);

  
  return (
    <div>
      <HomePage />
      <Form />
      <Output result={planResult} />
      {/* <Card /> */}
